import React, { Component } from "react";
import axios from "axios";
import ListArticle from "../components/list-article"
// import {
//     useParams
//   } from "react-router-dom";

// const apiKey = "448feafa267b4bf4babf102fe746d08b"
const baseUrl = "https://newsapi.org/v2/everything"
// const q = "academy"
// const urlHeadline = baseUrl + "?q=" + q + "&apiKey=" + apiKey;

class ListArticleData extends Component{
    state = {
        listListArticle: [],
        isLoading: true ,
        q: 'academy'
    };
    // handleRouterNav = categoryName => {
    //     const category = categoryName;
    //     this.props.history.replace("/news-category/" + category);
    // };
    componentDidMount = async() => {
        // const { q } = this.props.match.params.q
        // let q = useParams();
        // if(q){
        //     fetch(`/news-category/${q}`)
        //     .then((q) => {
        //         this.setState(() => ({ q }))
        //     })
        // }

        // const paramCategory = await this.props.match.params.q;
        // this.setState({
        //     q: paramCategory
        // });


        // console.log("cek props di listarticle", this.props)
        const category = this.props.match.params.q;
        console.log("cek props di listarticle", category)
        const self = this;
        axios
            .get(`${baseUrl}?q=${self.state.q}&apiKey=448feafa267b4bf4babf102fe746d08b`)
            .then(function(response){
                self.setState({listListArticle: response.data.articles, isLoading: false});
            })
            .catch(function(error){
                self.setState({isLoading: false});
            });
    };
    render(){
        const {listListArticle, isLoading} = this.state;
        const topHeadlines = listListArticle.filter(item => {
            if(item.content !== null && item.urlToImage !== null){
                return item;
            }
            return false;
        });
        const headlineNews = topHeadlines.map((item, key) => {
            return(
                <ListArticle
                    key = {key}
                    title = {item.title}
                    img = {item.urlToImage}
                    description = {item.description}
                    url = {item.url}
                    published = {item.publishedAt}
                />
            );
        });
        return(
            <div className="headlineNews">
                {isLoading ? <div style={{ textAlign: "center" }}>Loading...</div> : headlineNews}
            </div>
        );
    }
}
export default ListArticleData;
