import React from 'react';
import '../assets/css/bootstrap.min.css';
import '../assets/css/main.css';
import Header from '../components/header';
import { Redirect } from "react-router-dom";

import axios from "axios";

const baseUrl = "https://newsapi.org/v2/everything";
const apiKey = "448feafa267b4bf4babf102fe746d08b";
const Profile = props => {
    state = {
        listListArticle: [],
        isLoading: true ,
        q: 'academy'
    };
    handleInputChange = async event => {
        let value = event.target.value;
        await this.setState({ q: value });
        const self = this;
        if (value.length > 2) {
            try {
                const response = await axios.get(
                    baseUrl + "?q=" + value + "&apiKey=" + apiKey
                );
                self.setState({listListArticle: response.data.articles, isLoading: false});
                console.warn(response.data.articles, "haaaaaaaaaaaaaaaaa");
            } catch (error) {
                console.error(error);
            }
        };
    };
    categoryNews = async event => {
        const self = this;
        await axios
            .get(`${baseUrl}?q=${event}&apiKey=448feafa267b4bf4babf102fe746d08b`)
            .then(function(response){
                self.setState({listListArticle: response.data.articles, isLoading: false});
            })
            .catch(function(error){
                self.setState({isLoading: false});
            });
    }
    const is_login = JSON.parse(localStorage.getItem("is_login"));
    const email = localStorage.getItem("email");
    const full_name = localStorage.getItem("full_name");
    if (is_login === false) {
        return <Redirect to={{ pathname: "/login" }} />;
    }
    else{
        return (
            <div>
                <Header {...this.props} doSearch={event => this.handleInputChange(event)} doCategory={(e) => this.categoryNews(e)}
                    placeholder="ketik sesuatu"/>
                <div className="container">
                    <div className="margin-atas">
                    </div>
                    <div className="margin-atas">
                    </div>
                    <div className="row">
                        <div className="col-md-4">

                        </div>
                        <div className="col-md-4">
                            <h2 className="text-center">Profil</h2>
                            Email : {email}
                            <br></br>
                            Fullname : {full_name}
                        </div>
                    </div>
                </div>
            </div>   
        );
    }
}
export default Profile;
