import '../assets/css/bootstrap.min.css';
import '../assets/css/main.css';
import Header from '../components/header';
// import TopArticle from '../components/top-article';
// import ListArticle from '../components/list-article';
import ListArticleData from './list-article-data';
import TopArticleData from './top-article-data';

class Home extends Component{
    handleInputChange = async event => {
        let value = event.target.value;
        await this.setState({ search: value });
        this.searchNews(value);
    };

      searchNews = async keyword => {
        const self = this;
        if (keyword.length > 2) {
          try {
            const response = await axios.get(
              baseUrl + "everything?q=" + keyword + "&apiKey=" + apiKey
            );
            self.setState({ listNews: response.data.articles });
          } catch (error) {
            console.error(error);
          }
        }
    };
    render(){
        return (
            <div>
                <Header doSearch={event => this.handleInputChange(event)}
                    placeholder="ketik sesuatu"
                    {...this.props}/>
                <div className="container">
                    <div className="margin-atas">
                        <div className="row">
                            <div className="col-md-1">

                            </div>
                            <div className="col-md-4">
                                <TopArticleData/>
                            </div>
                            <div className="col-md-7">
                                <ListArticleData {...this.props}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>   
        );
    }
}
export default Home;
